//#include <stdio.h>size_t s21_strcspn(const char *str1, const char *str2) {    size_t count = 0;    while (*str1) {        const char *ptr = str2;        while (*ptr) {            if (*str1 == *ptr) {                return count;            }            ptr++;        }        str1++;        count++;    }    return count;}//int main() {//    const char *str1 = "hello world";//    const char *str2 = "aeiou";//    size_t result = s21_strcspn(str1, str2);//    printf("%zu\n", result);//    return 0;//}